(() => {
  const copyButtonStyles = {
    position: `relative`,
    border: `none`,
    padding: `10px 15px`,
    margin: `5px`,
    borderRadius: `5px`,
    fontSize: `26px`,
    boxShadow: `0 2px 2px 2px rgb(0 ,200, 200 , 0.25)`,
    cursor: `pointer`,
  };

  let div = document.createElement("div");
  let form = document.createElement("form");
  let div1 = document.createElement("div");

  //div style
  Object.assign(div.style, {
    top: "0px",
    zIndex: "12345",
    position: "fixed",
    width: `100%`,
    display: "flex",
    flexDirection: "column",
    flexWrap: "wrap",
    justifyContent: "center",
    alignItems: "center",
    alignContent: "center",
    background: `rgb(0 ,0, 0, 0.56)`,
    padding: `10px`,
  });

  let input = document.createElement("input");

  Object.assign(input.style, {
    border: `none`,
    padding: `10px 15px`,
    margin: `5px`,
    borderRadius: `5px`,
    fontSize: `26px`,
    boxShadow: `0 2px 2px 2px rgb(0 ,200, 200 , 0.25)`,
  });

  input.setAttribute("type", "text");
  input.setAttribute("name", "giveNumbers");
  input.setAttribute("placeholder", "1,2,..");

  let input1 = document.createElement("input");

  Object.assign(input1.style, {
    border: `none`,
    padding: `10px 15px`,
    margin: `5px`,
    borderRadius: `5px`,
    fontSize: `26px`,
    boxShadow: `0 2px 2px 2px rgb(0 ,200, 200 , 0.25)`,
    cursor: `pointer`,
  });

  input1.setAttribute("type", "submit");
  input1.setAttribute("value", "GET");

  //form
  form.appendChild(input);
  form.appendChild(input1);

  //local_check_copy_button_if_already_exists
  let local_check_cpy_button = [];

  //database
  let local_downloads_array_real_database = [];
  let index_ = 0;

  form.addEventListener("submit", (e) => {
    e.preventDefault();

    let giveNumbers = form.giveNumbers.value;
    let splitedNumber = giveNumbers.split(",");

    let localCheck = splitedNumber.sort();
    console.log(localCheck);

    let local_downloads_array = ``;
    let local_downloads_array_real = [];

    Array.from(
      document.querySelector("div.style-scope ytd-playlist-video-list-renderer")
        .children[2].children
    ).forEach((cl) => {
      if (cl.children[0].children[1] != undefined) {
        localCheck.forEach((ck, i) => {
          if (cl.children[0].children[1].textContent === ck) {
            if (!local_check_cpy_button.includes(parseInt(ck))) {
              //works
              let local_str =
                cl.children[1].children[0].children[1].children[0].children[1]
                  .href;
              let local_lastSlice = local_str.indexOf("&list=");

              let local_links = local_str.slice(0, local_lastSlice);

              local_downloads_array_real.push({
                index: index_,
                id: ck,
                link: local_links,
              });

              local_downloads_array_real_database.push({
                index: index_,
                id: ck,
                link: local_links,
              });

              local_check_cpy_button.push(parseInt(ck));

              if (i === 0) {
                local_downloads_array += `\n{ id: ${ck}, link: "${local_links}" },`;
              } else if (i > 0 && i + 1 < localCheck.length) {
                local_downloads_array += `\n{ id: ${ck}, link: "${local_links}" },`;
              } else if (localCheck.length === i + 1) {
                local_downloads_array += `\n{ id: ${ck}, link: "${local_links}" }\n`;
              }

              index_++;
            }
          }
        });
      }
    });

    let copy_localText = document.createElement("textarea");
    copy_localText.value = local_downloads_array;

    /* Select the text field */
    copy_localText.select();

    /* Copy the text inside the text field */
    navigator.clipboard.writeText(copy_localText.value);

    /* Alert the copied text */
    alert("Copied the text: " + copy_localText.value);

    let LocalArrayCopy = local_downloads_array_real;
    console.log(local_downloads_array_real_database);

    LocalArrayCopy.forEach((item) => {
      let button = document.createElement("button");

      Object.assign(button.style, copyButtonStyles);

      button.textContent = `copy link ${item.id}`;
      button.setAttribute("data-link-number", `${item.index}`);

      button.addEventListener("click", (e) => {
        e.target.style.background = `#00f`;

        let parsedInt = parseInt(e.target.dataset.linkNumber);
        console.log(parsedInt);

        let copy_localText1 = document.createElement("textarea");
        copy_localText1.value =
          local_downloads_array_real_database[parsedInt].link;

        /* Select the text field */
        copy_localText1.select();

        /* Copy the text inside the text field */
        navigator.clipboard.writeText(copy_localText1.value);

        /* Alert the copied text */
        alert("Copied the text: " + copy_localText1.value);
      });

      div1.appendChild(button);
    });

    form.giveNumbers.value = "";
  });

  //form style
  form.style.position = "relative";

  div.appendChild(form);
  div.appendChild(div1);
  document.body.appendChild(div);
})();
