var intCR_,
  avoid_ = false,
  avoid_url_load = false,
  pathNameNotDetected = true,
  setted_bandUrls = false,
  setted_remind_ayahs = false,
  urls = ["/feed/explore", "/shorts/"],
  bandUrls = [],
  remind_ayahs = [];

function CR_ckeck() {
  function commonReplacer() {
    let pthName = window.location.pathname;

    function setDom() {
      document.documentElement["style"]["background-color"] = "black";
      document.documentElement["style"]["color"] = "white";
      document.documentElement.innerHTML = `<pre style="font-size: 16px;white-space: pre-wrap;">${JSON.stringify(
        remind_ayahs,
        null,
        "  "
      )}</pre>`;
    }

    function detectBandSites() {
      for (let i = 0; i < bandUrls.length; i++) {
        const url = bandUrls[i];

        if (window.location.href.includes(url)) {
          if (avoid_url_load === false) {
            if (setted_remind_ayahs) {
              setDom();
            } else {
              (async (url) => {
                const response = await fetch(url);
                const data = await response.json();
                remind_ayahs = data;
                setted_remind_ayahs = true;

                setDom();
              })(
                "https://raw.githubusercontent.com/Afsinur/testfiles/master/common_reminder_ftc_ayahs.json"
              );
            }
          }

          avoid_url_load = true;
          break;
        } else {
          avoid_url_load = false;
        }
      }
    }

    for (let i = 0; i < urls.length; i++) {
      const url = urls[i];
      if (pthName.includes(url)) {
        if (avoid_ === false) {
          if (setted_remind_ayahs === false) {
            (async (url) => {
              const response = await fetch(url);
              const data = await response.json();
              remind_ayahs = data;
              setted_remind_ayahs = true;

              setDom();
            })(
              "https://raw.githubusercontent.com/Afsinur/testfiles/master/common_reminder_ftc_ayahs.json"
            );
          }
        }

        if (document.documentElement.childNodes[1].childNodes.length >= 2) {
          setDom();
        }

        pathNameNotDetected = false;
        avoid_ = true;
        break;
      } else {
        avoid_ = false;
      }
    }

    if (pathNameNotDetected) {
      if (setted_bandUrls === false) {
        (async (url) => {
          const response = await fetch(url);
          const data = await response.json();

          bandUrls = data.urls;
          setted_bandUrls = true;

          detectBandSites();
        })(
          "https://raw.githubusercontent.com/Afsinur/testfiles/master/almost_all_band_site_urls.json"
        );
      }

      if (document.documentElement.childNodes[1].childNodes.length >= 2) {
        detectBandSites();
      }
    }
  }

  commonReplacer();
}

intCR_ = setInterval(CR_ckeck, 1000);
